import React, { useState } from "react";
import classes from "./AddUsers.module.css";
import Card from "./UI/Card";
import Button from "./UI/Button";
// import Wrapper from "./helper/Wrapper";
import Errormodel from "./UI/Errormodel";
// import UserList from "./UsersList";
const AddUser = (props) => {
  const [enteredUsername, setEnteredUsername] = useState("");
  const [enteredAge, setEnteredAge] = useState("");
  const [error, setError] = useState();

  const addUserHandler = (event) => {
    event.preventDefault();
    if (enteredUsername.trim().length === 0 || enteredAge.trim().length === 0) {
      setError({
        title: "invalid input",
        message: "Please Enter the valid name",
      });
      return;
    }
    // JSX
    if (+enteredAge < 1) {
      setError({
        title: "invalid age",
        message: "Please Enter the valid age greater than 0",
      });
      // + ensures it is a number ;
      return;
    }
    props.onAddUser(enteredUsername, enteredAge);
    setEnteredUsername("");
    setEnteredAge("");
  };

  const usernameChangeHandler = (event) => {
    setEnteredUsername(event.target.value);
  };
  const AgeChangeHandler = (event) => {
    setEnteredAge(event.target.value);
  };

  const errorHandler = () => {
    setError(null);
  };

  //wraping an array is combursome
  return (
    <>
      {/* or use React.fragment  vert important wrapper wrappper from this things and apply for the same and take care of yourself pleas*/}
      {error && (
        <Errormodel
          title={error.title}
          message={error.message}
          onConfirm={errorHandler}
          // key="hey-bro"
        />
      )}
      <Card className={classes.input}>
        <form onSubmit={addUserHandler}>
          <label htmlFor="username">Username</label>
          <input
            id="username"
            type="text"
            value={enteredUsername}
            onChange={usernameChangeHandler}
          />
          <label htmlFor="username">Age</label>
          <input
            id="age"
            type="number"
            value={enteredAge}
            onChange={AgeChangeHandler}
          />
          <Button type="submit">Add User</Button>
        </form>
      </Card>
    </>
    // creating wraping components
    // div soup
  );
};

export default AddUser;
