import { useReducer } from "react";

const initialInputState = {
  value: "",
  enteredValuetouch: false,
};

const inputStateReducer = (state, action) => {
  return initialInputState;
};

const useInput = (Validatevalue) => {
  const [inputState, dispatch] = useReducer(
    inputStateReducer,
    initialInputState
  );

  const [enteredValue, setEnteredValue] = useState("");
  const [enteredValuetouch, setEnteredValuetouch] = useState(false);

  const enteredValueIsValid = Validatevalue(enteredValue);
  // const enteredAge
  const ValueInputIsInvalid = !enteredValueIsValid && enteredValuetouch;

  const ValueInputChangeHandler = (event) => {
    setEnteredValue(event.target.value);
  };

  const ValueInputBlurHandler = (event) => {
    setEnteredValuetouch(true);
  };

  const reset = () => {
    setEnteredValue("");
    setEnteredValuetouch(false);
  };

  return {
    value: enteredValue,
    isValid: ValueInputIsInvalid,
    ValueInputIsInvalid,
    ValueInputBlurHandler,
    ValueInputChangeHandler,
    reset,
  };
};

export default useInput;
