const redux = require("redux"); //importing redux is diferent in redux

const counterReducer = (state = { counter: 0 }, action) => {
  if (action.type === "increment") {
    return {
      counter: state.counter + 1,
    };
  }
  if (action.type === "decrement") {
    return {
      counter: state.counter - 1,
    };
  }
  // returns a object
}; //Reducer function for redux
// recive that by default
// takes two pieces of input
const store = redux.createStore(counterReducer);

// console.log(store.getState());

const counterSubscriber = () => {
  const latestState = store.getState(); // this function will executed when ever state will change
  console.log(latestState);
};

store.subscribe(counterSubscriber);

store.dispatch({ type: "increment" }); //dispatch leads to code run again
store.dispatch({ type: "decrement" });
