import React from "react";
import mealsImage from "../../assets/meals.jpg";
// you can import images from there directory
// if you import fragment then it can be used directly as Fragment
import classes from "./Header.module.css";
import HeaderCartButton from "./HeaderCartButton";

const Header = (props) => {
  return (
    <React.Fragment>
      <header className={classes.header}>
        <h1>ReactMeals</h1>
        {/* <button>Cart</button>
         */}
      </header>
      <div className={classes["main-image"]}>
        <img
          src={mealsImage}
          alt="A table of dinner"
          className={classes["main-image img"]}
        />
        {/* in comma you can add url */}
      </div>

      {/* you must have one root element */}
    </React.Fragment>
  );
};

export default Header;
