import { Component } from "react";

class Error extends Component {
  constructor() {
    super();

    this.state = { hasError: flase };
  }

  componentDidCatch(error) {
    this.setState({ hasError: true });
  }

  render() {
    if(this.state.hasError)
    return this.props.children;
  }
}

export default Error;
